mapper.delete:
{
  CALL
    BEGIN
   	  DELETE FROM pro_list WHERE pno = #{pno};
      DELETE FROM product WHERE pno = #{pno};
    END
}

mapper.update:
update product
set readCount = readCount +1
where pno = #{pno}

update pro_list
set title = #{title},
contents = #{contents},
price = #{price}
where code = #{code}
